
public class Assign4 {

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub

		int x=5,y=10,exp1,exp2,exp3,exp4;
		exp1=x+y*2;
		exp2=x-y+2;
		exp3=(x+y)*2;
		exp4=y % x;
		
		System.out.println("Result of the expression (a)X+Y*2:"+ exp1);
		System.out.println("Result of the expression (b)X-Y+2:"+ exp2);
		System.out.println("Result of the expression (c)(X+Y)*2:"+ exp3);
		System.out.println("Result of the expression (d)y%x:"+ exp4);
	}
	

}
